				f[i][j]=f[i-1][j-1];
				if(i==j&&i>=b)Add(f[i][j],1);
				if(j>=b+1)Add(f[i][j],sf[i-j][j-(b+1)]);
			} 
			sf[i-j][j]=(!j?0:sf[i-j][j-1]);
			Add(sf[i-j][j],f[i][j]);
			if(j+1<a)Add(sum[i],f[i][j]);
		}
	}
	for(int i=0;i<a;i++)ans=(ans+mod-f[n][i])%mod;
	printf("%d\n",ans);
