void Sub(int &res, int x) { res = (res - x + mod) % mod; }

int main()
{
    pw2[0] = 1;
    for (int i = 1; i < Maxn; i++)
        pw2[i] = 2ll * pw2[i - 1] % mod;
    scanf("%d %d %d", &n, &a, &b);
    if (a > b) swap(a, b);
    Add(delt[0][1], 1);
    Sub(delt[0][2], 1);
    for (int i = 0; i <= n; i++) {
        for (int j = i + 1; j <= n + 1; j++) {
            int cur = 0;
            if (j > 0) Add(delt[i][j], delt[i][j - 1]);
            Add(cur, delt[i][j]);
            if (j == i + 2) {
                if (i > 0) Add(ind[i], ind[i - 1]);
                Add(cur, ind[i]);
            }
            if (j - 1 - i >= b) { Add(res, ll(cur) * pw2[n + 1 - j] % mod); continue; }
            Add(delt[i][j + 1], cur);
            Sub(delt[i][j + 2], cur);
            int lim = min(a, n + 1 - j);
            Add(ind[j], cur);
            Sub(ind[j + lim - 1], cur);
            if (j + a <= n + 1) {
                if (j - 1 - i + a >= b) {
                    Add(delt[i][j + a], cur);
                    Sub(delt[i][j + a + 1], cur);
                } else {
                    Add(delt[i][j + a + 1], cur);
                    Add(delt[i][n + 1], cur);
                }
            }
        }
    }
    printf("%d\n", res);
    return 0;
}

