      }
    }
  }
  ll ans1 = 0;
  rep(i,0,n){
    ll d = n-i;
    if(d<a){
      ans1 += dp[i][0];
      ans1 %= MOD;
    }
    if(d<b){
      ans1 += dp[i][1];
      ans1 %= MOD;
    }    
  }
