mint func4(int i, int a_max, int onB)
{
	if (N <= a_max) {
		return 0_mi;
	}
	if (a_max < i) {
		return pow2[N-i];
	}
	if (i >= N) {
		return 0_mi;
	}

	auto& memo = dp4[i][a_max][onB];
	if (used4[i][a_max][onB]) {
		return memo;
	}
	used4[i][a_max][onB] = true;

	if (onB!=0) {
		return memo = func4(i + 1, a_max, 0) + func4(i + 1, a_max, 1);
	}

	memo = func4(i + 1, a_max, 0);
	//for (size_t c = 1; c < B; c++)
	//{
	//	memo += func4(i + c + 1, i + c + A-1, 0);
	//}
	memo += func5(i + 1, std::min(i + B,N));
	memo += func4(i + B, a_max, 1);
	return memo;
}

//A[a_end-A, a_end)
mint func(int i, int a_end, bool prev_is_b)
{
	static mint dp[5100][5100][2];
	static bool used[5100][5100][2];
	if (N < i || N + 1 <= a_end) {
		return 0_mi;//
	}
	if (a_end <= i) {
		return pow2[N - i];//
	}
	assert(i <= N);

	auto& memo = dp[i][a_end][prev_is_b?0:1];
	if (used[i][a_end][prev_is_b ? 0 : 1]) {
		return memo;
	}
	used[i][a_end][prev_is_b ? 0 : 1] = true;

	mint res = 0;//mintMOD

	if (prev_is_b) {
		//B
		res += func(i + 1, a_end, false);//0
		res += func(i + 1, a_end, true);//1
	}
	else {
		//A
		res += func(i + 1, a_end, false);//0
		for (int k = 1; k < B; k++) {//1k0
			//B0
			res += func(i + k + 1, i + k + A, false);
			//0A
		}
		res += func(i + B, a_end, true);//1B
	}
	return memo = res;
}

//A[a_end-A, a_end), B[b_end-B, b_end)
mint func_ON3(int i, int a_end, int b_end)
{
	static mint dp[100][100][100];
	static bool used[100][100][100];
	if (N+1 <= a_end || N + 1 <= b_end) {
		return 0_mi;//
	}
	if (a_end <= i && b_end <= i) {
		return pow2[N - i];//
	}

	auto& memo = dp[i][a_end][b_end];
	if (used[i][a_end][b_end]) {
		return memo;
	}
	used[i][a_end][b_end] = true;

	mint res = 0;//mintMOD
	if (i < b_end) {//B
		res += func_ON3(i + 1, i + A, 0);//0
		res += func_ON3(i + 1, a_end, b_end);//1
	}
	else if (B <= b_end && i == b_end) {//B
		res += func_ON3(i + 1, a_end, b_end);//0
		res += func_ON3(i + 1, a_end, b_end+1);//1
	}
	else {//A
		res += func_ON3(i + 1, a_end, b_end);//0
		res += func_ON3(i + 1, a_end, i + B);//1
	}
	return memo = res;
}

