constexpr int md = 1e9 + 7;
using Mint = Modular<std::integral_constant<decay<decltype(md)>::type, md>>;

Mint pwr(Mint a, ll k) {
	Mint ans = 1;
	while(k) {
		if(k & 1) ans = ans * a;
		a = a * a;
		k >>= 1;
	}
	return ans;
}

const int N = 5005;
Mint dp[N][N][2][2];
Mint diag_dp[N][2];

void solve() {
	int n, A, B;
    sc(n, A, B);
    if(A < B) swap(A, B);
    dp[0][0][0][0] = 1;
    dp[0][0][1][0] = 1;
    dp[1][0][0][0] = -1;
    dp[1][0][1][0] = -1;
    fr(i, 0, n) {
        if(i) {
            diag_dp[i][0] += diag_dp[i - 1][0];
            diag_dp[i][1] += diag_dp[i - 1][1];
        }
        fr(j, 0, i) {
            fr(k, 0, 1) {
                fr(l, 0, 1) {
                    Mint &val = dp[i][j][k][l];
                    if(i)
                        val += dp[i - 1][j][k][l];
                    if(i == j && k) {
                        val += diag_dp[i][l];
                        dp[i + 1][j][k][l] -= diag_dp[i][l];
                    }
                    if(!val) continue; // speed up
                    if(k) {
                        dp[i + 1][j][0][l] += val; // good
                    }
                    else {
                        diag_dp[i + 1][(l | (i - j) >= A)] += val;
                        if(i + B <= n) {
                            diag_dp[i + B][(l | (i - j) >= A)] -= val;
                            dp[i + B][j][1][l] += val;
                        }
                    }
                }
            }
        }
    }
    Mint ans = 0;
    fr(j, 0, n) {
        ans += dp[n][j][0][1] + dp[n][j][1][1];
        if(n - j >= A) ans += dp[n][j][0][0] + dp[n][j][1][0];
    }
    cout << ans;
}

int main() {
	ios :: sync_with_stdio(0);
	cin.tie(0);
	cout.tie(0);
	int t = 1;
	// cin >> t;
	for(int tt = 1; tt <= t; tt++) {
		solve();
	}
	return 0;
}

