vector<Mint> fact(1, 1);
vector<Mint> inv_fact(1, 1);

Mint C(int n, int k) {
  if (k < 0 || k > n) {
    return 0;
  }
  while ((int) fact.size() < n + 1) {
    fact.push_back(fact.back() * (int) fact.size());
    inv_fact.push_back(1 / fact.back());
  }
  return fact[n] * inv_fact[k] * inv_fact[n - k];
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n, a, b;
  cin >> n >> a >> b;
  if (a > b) {
    swap(a, b);
  }
  vector<vector<Mint>> aux(n + 1, vector<Mint>(n + 1, 0));
  aux[0][0] = 1;
  for (int i = 0; i < n; i++) {
    for (int j = 0; j <= i; j++) {
      aux[i + 1][j + 1] += aux[i][j];
      if (j == 0 || j >= a) {
        aux[i + 1][0] += aux[i][j];
      }
    }
  }
  vector<Mint> help(n + 1);
  for (int i = 0; i <= n; i++) {
    for (int j = 0; j <= i; j++) {
      if (j == 0 || j >= a) {
        help[i] += aux[i][j];
      }
    }
  }
  vector<Mint> dp(n);
  Mint ans = 0;
  for (int L = 0; L < n; L++) {
    for (int R = L; R < n && R - L + 1 < a; R++) {
      if (L < b) {
        dp[R] += help[max(L - 1, 0)];
      }
    }
  }
  vector<Mint> add(n + 1, 0);
  Mint delta = 0;
  for (int i = 0; i < n; i++) {
    delta += add[i];
    dp[i] += delta;
    for (int L = i + 2; L < n; L++) {
      if (L - i - 1 < b) {
        int from = L;
        int to = min(n - 1, a - 2 + L);
        Mint val = dp[i] * help[max(L - i - 3, 0)];
        add[from] += val;
        add[to + 1] -= val;
      }
    }
  }
  for (int i = 0; i < n; i++) {
    if (n - 1 - i < b) {
      ans += dp[i] * help[max(n - 2 - i, 0)];
    }
  }
  ans = power(Mint(2), n) - ans;
  cout << ans << '\n';
