				else {
					if(i == 0 && i + h == n) f[i + h][k ^ 1] = (f[i + h][k ^ 1] + (LL)f[i][k] * z[h]) % mod;
					else if(i == 0 || i + h == n) f[i + h][k ^ 1] = (f[i + h][k ^ 1] + (LL)f[i][k] * g[h]) % mod;
					else f[i + h][k ^ 1] = (f[i + h][k ^ 1] + (LL)f[i][k] * T[h]) % mod;
				}
			}
		}
	} bin[0] = 1; fo(i, 1, n) bin[i] = bin[i - 1] << 1, bin[i] = bin[i] >= mod ? bin[i] - mod : bin[i];
	reg ans = bin[n];
	ans = ((LL)ans - f[n][0] - f[n][1]) % mod;
	pr2(ans < 0 ? ans + mod : ans);
	return 0;
}

