    if (a>b) std::swap(a, b);

    auto fp = mkvec<2, mint>(2, b);
    fp.at(0).at(0) = 1;
    fp.at(1).at(0) = 1;
    for (usize i=0; i<b; i++) {
        for (usize j=a; i+j<b; j++) fp.at(0).at(i+j) += fp.at(1).at(i);
        for (usize j=1; i+j<b; j++) fp.at(1).at(i+j) += fp.at(0).at(i);
    }

    vec<mint> ep(b);
    ep.at(0) = 1;
    for (usize i=1; i<b; i++) {
        ep.at(i) = fp.at(0).at(i) + fp.at(1).at(i);
    }

    auto dp = mkvec<2, mint>(2, n);
    for (usize j=1; j<a; j++) dp.at(0).at(j) += 1;
    for (usize j=1; j<b; j++) dp.at(1).at(j) += ep.at(j-1);

    for (usize i=1; i<n; i++) {
        for (usize j=1; j<a && i+j<n; j++) dp.at(0).at(i+j) += dp.at(1).at(i);
        for (usize j=1; j<b && i+j<n; j++) dp.at(1).at(i+j) += dp.at(0).at(i) * ep.at(j==1?1:j-2);
    }

    mint ans = 0;
    for (usize j=1; j<b; j++) ans += dp.at(0).at(n-j) * ep.at(j-1);
    for (usize j=1; j<a; j++) ans += dp.at(1).at(n-j);
    ans = mint::pow(2, n) - ans;
    std::cout << ans << '\n';
}

