using namespace std;


ll mpow(ll x, ll n){ //x^n(mod M)
    ll ans = 1;
    while(n != 0){
        if(n&1) ans = ans*x % MOD;
        x = x*x % MOD;
        n = n >> 1;
    }
    return ans;
}

ll minv(ll x){
    return mpow( x, MOD-2 );
}


ll dp[5005][5005][2];

void solve()
{
    int n, a, b;
    scanf("%d%d%d", &n, &a, &b);

    if (a < b) swap(a, b);

    if (a >= b) {
        dp[0][0][0] = dp[0][0][1] = 1;
        int i, j, k;
        for (i = 0; i < n; i++) {
            for (j = 0; j < a; j++) {
                for (k = 0; k < 2; k++) {
                    if(dp[i][j][k]) {
                        int m;
                        for (m = 1; m < MIN(a,n-i+1); m++) {
                            int i2 = i + m;
                            assert(i2 <= n);
                            int j2 = m + j;
                            if (k == 0) {
                                ;
                            }
                            else {
                                if (m < b) j2 = 0;
                                else       j2 = j + m;
                            }
                            if (j2 >= a) continue;

                            dp[i2][j2][1 - k] = (dp[i2][j2][1 - k] + dp[i][j][k]) % MOD;
                        }
                    }
                }
            }
        }

        ll ans = 0;
        for (j = 0; j < a; j++) {
            for (k = 0; k < 2; k++) {
                ans = (ans + dp[n][j][k]) % MOD;
            }
        }
        ans = (mpow(2, n) - ans + MOD) % MOD;
        printf("%lld\n", ans);
    }
    return;
}


