                if (j == i) dp2[i][1] += dp[j][0];
            }
        }
        dp2[i][0] %= MOD,dp2[i][1] %= MOD;
    }
    int p = 1;
    for (i = 0; i < N; i++) p *= 2,p %= MOD;
    printf("%lld\n",(p-(dp2[N][0]+dp2[N][1])+2*MOD) % MOD);

    return 0;
}

