};


int main() {
  ll n, a, b; cin >> n >> a >> b;
  if (a > b) swap(a, b);

  Polynomial<mint> p(n+1), q(n+1), f(n+1);
  p[a] = 1;
  p.div({{0, 1}, {1, -1}});
  q[1] = 1;
  q.div({{0, 1}, {1, -1}});
  f[1] = 1;
  f[a] -= 1;
  f.div({{0, 1}, {1, -1}});

  auto r = -p * q;
  r[0] += 1;

  auto g2 = q / r;
  auto g1 = q;
  g1[0] += 1;
  g1 /= r;
  auto g0 = p + q;
  g0[0] += 2;
  g0 /= r;

  rep2(i, b, n+1) g0[i] = 0;
  rep2(i, b, n+1) g1[i] = 0;
  rep2(i, b, n+1) g2[i] = 0;

  auto h = f;
  h *= g1 * g1;
  auto h0 = -f * g2;
  h0[0] += 1;
  h /= h0;
  h += g0;

  mint ans = mint(2).pow(n) - h[n];
  cout << ans << "\n";
  return 0;
}

