inline int pls( int a , int b ) { a += b - mod; return a + ( a >> 31 & mod ); }
inline int mns( int a , int b ) { a -= b; return a + ( a >> 31 & mod ); }
inline void inc( int & a , int b ) { a += b - mod; a += a >> 31 & mod; }
inline void dec( int & a , int b ) { a -= b; a += a >> 31 & mod; }

int A , B , n , ans;
int f[N] , g[N][2];

int main( void ) {
  cin >> n >> A >> B; 
  if( A > B ) swap( A , B );
  ans = 1;
  for( int i = 1 ; i <= n ; ++i ) ans = pls( ans , ans );
  if( B == 1 ) return printf("%d",ans) , 0;
  f[0] = 1;
  for( int i = 1 ; i <= B ; ++i ) {
    f[i] = f[i - 1];
    for( int j = A ; j <= i ; ++j ) {
      if( j ^ i ) inc( f[i] , f[i - j - 1] );
      else inc( f[i] , 1 );
    }
  }
  g[0][0] = 1;
  for( int i = 1 ; i < B ; ++i )
    g[i][0] = f[i - 1];
  for( int i = 1 ; i <= n ; ++i ) {
    for( int j = 1 ; j < B && j <= i ; ++j )
      inc( g[i][0] , 1LL * g[i - j][1] * ( j == 1 ? 1 : f[j - 2] ) % mod );
    for( int j = 1 ; j < A && j <= i ; ++j )
      inc( g[i][1] , g[i - j][0] );
  }
  dec( ans , g[n][1] );
  for( int i = 1 ; i < B && i <= n ; ++i )
    dec( ans , 1LL * g[n - i][1] * f[i - 1] % mod );
  printf("%d",ans);
  return 0;
}

