int inc(int a, int b) {return (a += b) >= mod ? a - mod : a;}

int main()
{
	int n, a, b; scanf("%d%d%d", &n, &a, &b);
	if(a > b) std::swap(a, b);
	mi[0] = 1; for(int i = 1; i <= n; i++) mi[i] = mi[i - 1] * 2 % mod;
	f[0][0][0] = f[0][0][1] = s[0] = 1;
	sum[0][1] = 1;
	int ans = 0;
	for(int i = 1; i <= n; i++)
	{
		for(int j = 0; j <= std::min(b, i); j++)
		{
			if(j == b)
				ans = (ans + 1ll * mi[n - i] * (sum[i - j][0] + sum[i - j][1])) % mod;
			else
			{
				f[i][j][0] = sum[i - j][1];
				f[i][j][1] = sum[i - j][0];
				s[i] = inc(s[i], f[i][j][0]);
			}
		}
		for(int j = std::max(i - a + 1, 0); j < i; j++)
			f[i][0][1] = inc(f[i][0][1], s[j]);
		if(i - a >= 0)
		{
			for(int j = 0; j <= i - a; j++)
			{
				if(j + a < b)
					f[i][j + a][1] = inc(f[i][j + a][1], f[i - a][j][0]);
				else 
					ans = (ans + 1ll * mi[n - i] * f[i - a][j][0]) % mod;

				sum[i - a - j][0] = inc(sum[i - a - j][0], f[i - a][j][0]);
			}
		}
		for(int j = 0; j <= i; j++)
			sum[i - j][1] = inc(sum[i - j][1], f[i][j][1]);
	}
	printf("%d\n", ans);
}

