int const nax = 3e5 + 10;
constexpr int mod = 1e9 + 7;//
void plusle (int &a, int b) {
    if (( a += b) >= mod ) a -= mod ;
}
void minun (int &a, int b) {
    if ((a -= b) < 0) a += mod ;
}
int add (int a, int b) {
    return (( a += b ) >= mod ) ? a - mod  : a;
}
int sub (int a, int b) {
    return (( a -= b) < 0) ? a + mod : a;
}
int mul (int a, int b) {
    return 1LL * a * b % mod;
}
int power (int a, int b) {
    int res = 1;
    for (; b > 0 ; b >>= 1 , a = mul(a, a))
        if (b & 1) res = mul(res, a);
    return res ;
}

int dp[nax];
int a, b;
int n;
int f[nax];
int pref[nax];

int rec (int sz) {

    if (sz == 0) return 1;
    if (dp[sz] != -1) return dp[sz];
    /// the number of different substring of this size

    for (int i = 1 ; i < sz ; ++ i) f[i] = rec(i);
    pref[0] = 1;

    for (int i = 1 ; i < sz ; ++ i) {
        pref[i] = add(pref[i - 1], f[i]);
    }

    dp[sz] = 1; /// all 0

    for (int i = a ; i <= sz ; ++ i) {
        int res = sz - i;
        plusle(dp[sz], pref[res]);
        if (sz == 4) {
            fprintf(stderr, "add %d\n", pref[res]);
        }
    }

    for (int st = 2 ; st <= sz ; ++ st) {
     //   if (st + a - 1 > sz) break; /// necessary
        if (st - 1 < b) {
            /// we cannot use 0 move on left
            int to = st + a - 1;
            if (to > sz) continue;
            int first_one = min(to - 1, sz - b);
            if (first_one < st) continue;
            int l = sz - first_one;
            int r = sz - st;
            plusle(dp[sz], sub(pref[r], l == 0 ? 0 : pref[l - 1]));
            if (sz == 4) {
                fprintf(stderr, "%d\n", st);
                fprintf(stderr, "%d %d\n", l, r);
                fprintf(stderr, "st - 1 < b add %d\n", sub(pref[r], l == 0 ? 0 : pref[l - 1]));
            }
        } else if (st - 1 >= b) {
            /// if we apply b from both side
            int last_one = sz - b;
            int first_one = st;
            int l = sz - last_one;
            int r = sz - first_one;
            if (first_one <= last_one ) {
                plusle(dp[sz], sub(pref[r], l == 0 ? 0 : pref[l - 1]));
            }

            /// if we apply from left only
            int to = min(sz, st + a - 1);
            int from = max(st, a);
            l = sz - to;
            r = sz - from;
            if (l <= r) {
                fprintf(stderr, "Add %d\n", sub(pref[r], l == 0 ? 0 : pref[l - 1]));

                plusle(dp[sz], sub(pref[r], l == 0 ? 0 : pref[l - 1]));
            }
        }
    }

    return dp[sz];
}




