void dec(int &a,int b) {a-=b; if(a<0) a+=MOD;}
int get_sum(int l1,int r1,int l2,int r2)
{
    int x=s[r1][r2];
    dec(x,s[l1-1][r2]);
    dec(x,s[r1][l2-1]);
    add(x,s[l1-1][l2-1]);
    return x;
}
int get_diag(int x,int y)
{
    return diag[x][y];
}
int main()
{
    scanf("%d%d%d",&n,&a,&b);
