            par[i-j][i][0]=par[i-j][i-1][0]+dp[i][j][0];
            par[i-j][i][1]=par[i-j][i-1][1]+dp[i][j][1];
            par[i-j][i][0]%=mod;
            par[i-j][i][1]%=mod;
        }
    }
    for (int i=0;i<b;i++){
        ans-=dp[n][i][0]+dp[n][i][1];
        ans+=mod*2;
        ans%=mod;
    }
    cout << ans << endl;
}

