    if (a < b) swap(a, b);
    if (b == 1) {
        cout << powmod(2, n) << endl;
        return 0;
    }
    VI bb(n + 3, 0);
    VI aa(n + 3, 0);
    for (i = 1; i <= b + 1; i++) bb[i] = i;
    for (i = b + 2; i <= n + 2; i++) {
        bb[i] = 1 + bb[i - 1] + bb[i - 1 - b];
        bb[i] %= mod;
    }
    for (i = n + 2; i >= 1; i--) {
        aa[i] = mod + bb[i] - bb[i - 1];
        aa[i] %= mod;
    }

    VVI dp1(n + 1, VI(a, 0));
    VVI dp2(n + 1, VI(b, 0));
    VVI dp3(n + 1, VI(a, 0));
    ll ans = 0;
    for (i = 0; i < n; i++) {
        if (i == 0) {
            // dp1[i][1] = 1;
            dp2[i][1] = 1;
            dp3[i][1] = 1;
        } else {
            for (j = 1; j < a; j++) {
                dp2[i][1] += dp1[i - 1][j] * aa[j];
                dp2[i][1] %= mod;
                dp2[i][1] += dp3[i - 1][j] * aa[j + 1];
                dp2[i][1] %= mod;
            }
            for (j = 1; j < b; j++) {
                dp1[i][1] += dp2[i - 1][j];
                dp1[i][1] %= mod;
            }
            for (j = a - 1; j >= 2; j--) {
                dp1[i][j] = dp1[i - 1][j - 1];
                dp3[i][j] = dp3[i - 1][j - 1];
            }
            for (j = b - 1; j >= 2; j--) dp2[i][j] = dp2[i - 1][j - 1];
        }
    }
    for (j = 1; j < a; j++) {
        ans += dp1[n - 1][j] * aa[j + 1];
        ans %= mod;
        ans += dp3[n - 1][j] * aa[j + 2];
        ans %= mod;
    }
    for (j = 1; j < b; j++) {
        ans += dp2[n - 1][j];
        ans %= mod;
    }
    cout << (powmod(2, n) + mod - ans) % mod << endl;
}

