const int N = 5005, MOD = 1e9 + 7;
int n, a, b, dp[N][N], ps[N], p[N][N];

void add(int & x, int y) {
	x += y;
	if (x >= MOD) x -= MOD;
}

int get(int l, int r, int k) {
	if (r < 0) return 0;
	if (k == -1)
		return (ps[r] - ((l > 0) ? ps[l - 1] : 0) + MOD) % MOD;
	return (p[r][k] - ((l > 0) ? p[l - 1][k] : 0) + MOD) % MOD;
}

int main() {
	ios_base::sync_with_stdio(false), cin.tie(NULL);
	#ifdef LOCAL
		freopen("input.txt", "r", stdin);
	#endif

	cin >> n >> a >> b;
	if (a > b) swap(a, b);
	dp[0][0] = ps[0] = p[0][0] = 1;
	for (int i = 1; i <= n + 1; i++) {
		for (int j = 0; j <= i; j++) {
			if (min(n, i) - max(1, j) + 1 >= b) continue;
			add(dp[i][j], dp[i - 1][j]);
			add(dp[i][j], get(0, i - a - 1, j));
		}
		add(dp[i][i], get(i - a, i - 2, -1));
		ps[i] = ps[i - 1];
		for (int j = 0; j <= n; j++) {
			add(ps[i], dp[i][j]);
			p[i][j] = p[i - 1][j];
			add(p[i][j], dp[i][j]);
		}
	}
	int res = 0, pw = 1;
	for (int i = 0; i < n; i++) pw = pw * 2 % MOD;
	for (int i = 0; i <= n + 1; i++) {
		add(res, dp[n + 1][i]);
	}
	cout << (pw - res + MOD) % MOD;
}

