const std::size_t SIZE = 5010 * 2;

mdarray<mint, SIZE, SIZE, 2> dp;

int main() {
    ll n, a, b;
    std::cin >> n >> a >> b;
    if (a > b) std::swap(a, b);

    mint ans = 0;
    dp[0][0][1] = 1;
    for (ll i = 0; i < n; i++) {
        mint one = 0;
        for (ll j = 0; j < b; j++) {
            dp[i + 1][j + 1][1] += dp[i][j][0] + dp[i][j][1];
            dp[i + 1][j + 1][0] += dp[i][j][0];
            dp[i + a][j + a][0] += dp[i][j][1];
            one += dp[i][j][1];
        }
        for (ll j = 2; j <= a; j++) {
            dp[i + j][1][1] += one;
        }
        for (ll j = b; j <= n; j++) {
            mint add = dp[i + 1][j][0] + dp[i + 1][j][1];
            ans += add * pow(mint(2), n - i - 1);
        }
    }

    std::cout << ans.value() << "\n";
    return 0;
}

