const int SIZE = 5014;
LL dp[2][SIZE][SIZE],dp2[SIZE][SIZE];
LL sum[2][SIZE];
void solve() {
    int N,A,B;
    R(N,A,B);
    if(A>B)swap(A,B);
    dp[1][0][0]=1;
    sum[1][0]=1;
    dp2[0][0]=1;
    FOR(i,1,N){
        FOR(j,1,min(i,A-1)){
            ADD(dp[0][i][0],sum[1][i-j]);
        }
        FOR(j,A,i){
            int gg=min(j-A,B-1);
            LL v=dp2[i-(j-gg)][gg];
            if(j>=B)ADD(dp[0][i][B],v);
            else ADD(dp[0][i][j],v);
        }
        FOR(j,0,i-1)ADD(dp[0][i][B],sum[0][j]);
        FOR(j,0,min(B,i-1)){
            ADD(dp[1][i][min(B,j+1)],dp[0][i-1][j]+dp[1][i-1][j]);
        }
        FOR(j,0,B){
            dp2[i][j]=dp[1][i][j];
            if(j)ADD(dp2[i][j],dp2[i-1][j-1]);
            if(j<B){
                ADD(sum[1][i],dp[1][i][j]);
            }
            else sum[0][i]=dp[1][i][j];
        }
    }
    LL an=dp[0][N][B]+dp[1][N][B];
    if(an>=MOD)an-=MOD;
    W(an);
}
