            }
        }
        REP(j,B+1,i){
            memo[i+2]+=dp[i][j];
            memo[i+2]%=MOD;
        }
        memo[i+2]+=dp[i][0];
        memo[i+2]%=MOD;
    }
    vii dp(N+1,vi(A+1)),cp(N+1,vi(B+1));
