    black_one[0] = 1;
    rep(i,N) {
        for (int j = A; i+j <= N; j++) {
            whilte_one[i+j] += black_one[i];
        }
        black_one[i+1] += black_one[i]+whilte_one[i];
    }

    vector<mint> dp_black(N+1);
    auto dp_white = dp_black;
    dp_white[0] = dp_black[0] = 1;
    rep(i,N) {
        for (int j = 1; j < A; j++) {
            if (i+j > N) continue; 
            dp_white[i+j] += dp_black[i];
        }
        for (int j = 1; j < B; j++) {
            if (i+j > N) continue;
            int len = j-2;
            if (i==0) len++;
            if (i+j==N) len++;
            chmax(len,0);
            dp_black[i+j] += dp_white[i]*(black_one[len]+whilte_one[len]);
        }
    }

    mint ans = 1;
    rep(i,N) ans *= 2;
    cout << ans - (dp_white[N]+dp_black[N]) << ln;
