	using namespace std;
	ios_base::sync_with_stdio(false), cin.tie(nullptr);

	using num = modnum<int(1e9)+7>;

	int N, A, B; cin >> N >> A >> B;
	assert(A <= N && B <= N);
	if (A > B) swap(A, B);
	assert(A <= B);

	// with bounding 1's
	vector<num> bways(N+3);
	bways[1] = 1;
	for (int i = 1; i+1 < int(bways.size()); i++) {
		bways[i+1] += bways[i];
		for (int j = A; i + j + 1 < int(bways.size()); j++) {
			bways[i+j+1] += bways[i];
		}
	}

	vector<array<num, 2>> totWays(N+3);
	for (int j = 1; j-1 < B; j++) {
		totWays[j][0] = bways[j];
	}
	for (int i = 0; i < N+2; i++) {
		for (int j = 1; i+j <= N+2 && j < A; j++) {
			totWays[i+j][1] += totWays[i][0];
		}
		for (int j = 1; i+j <= N+2 && j < B; j++) {
			totWays[i+j][0] += totWays[i][1] * bways[j];
		}
		if (i + B == N+2) {
			totWays[i+B][0] += totWays[i][1] * bways[B];
		}
	}

	cout << pow(num(2), N) - totWays[N+2][0] << '\n';

	return 0;
}

