	if (a >= mod)
		a -= mod;
}

int main()
{
	scanf ("%d %d %d", &n, &a, &b);

	long long all = 1;
	for (int i = 0; i < n; i++) all = all * 2 % mod;

	long long ans = 0;

	if (a > 1 && b > 1){
		if (a < b) swap(a, b);
		d[0][1] = 1;
		for (int i = 1; i < a; i++){
			add(d[0][i + 1], d[0][i]);
			add(d[0][i + 1], d[1][i]);
			if (i + b < a) add(d[1][i + b], d[0][i]);
			add(d[1][i + 1], d[1][i]);
		}

		e[0][0] = e[1][0] = 1;
		for (int j = 1; j < a; j++) e[0][j] = d[1][j];
