const ll maxn = 5010;

ll N, A, B;
ll dp1[maxn]; // B blocks starting and ending at b
ll dp2[maxn]; // complement strings ending at b

int main() {
    ios_base::sync_with_stdio(false), cin.tie(0), cout.tie(0);

    cin >> N >> A >> B;
    if (A > B) swap(A, B);

    dp1[1] = 1;
    for (ll i = 2; i <= B; ++i) {
        dp1[i] = dp1[i - 1];
        for (ll j = A; i - 1 - j >= 1; ++j) Add(dp1[i], dp1[i - 1 - j]);
    }
    dp2[0] = 1;
    for (ll i = 1; i <= N; ++i) {
        for (ll j = 1; j < B; ++j) {
            if (i == j) {
                Add(dp2[i], dp1[j + 1]);
            } else {
                ll s = 0;
                for (ll k = i - j - 1; k >= 0 && (i - j) - k < A; --k) {
                    Add(s, dp2[k]);
                }
                Add(dp2[i], 1LL * dp1[j] * s);
            }
        }
    }

    ll sum = 0;
    for (ll i = N; i > N - A; --i) Add(sum, dp2[i]);
    for (ll i = N - A; i - 1 > N - B; --i) {
        for (ll j = i; j - 1 > N - B; --j) {
            ll s = 0;
            for (ll k = j - 1; k >= 1 && j - k < A; --k) {
                Add(s, dp2[k - 1]);
            }
            Add(sum, 1LL * dp1[i - j + 1] * s);
        }
    }

    ll all = 1;
    for (ll i = 1; i <= N; ++i) Add(all, all);

    ll ans = (all + MOD - sum) % MOD;
    cout << ans << '\n';

}

