int Sum(int a, int b) {
    return (a + b) % P;
}

int Mult(int a, int b) {
    return (a * (long long) b) % P;
}

int Pow(int a, int n) {
    if (n == 0) {
        return 1;
    }

    int res = Pow(a, n / 2);
    res = Mult(res, res);
    if (n % 2 == 1) {
        res = Mult(res, a);
    }

    return res;
}

