template <int MOD>
struct Fp
{
  long long val;

  constexpr Fp(long long v = 0) noexcept : val(v % MOD)
  {
    if (val < 0)
      v += MOD;
  }

  constexpr int getmod()
  {
    return MOD;
  }

  constexpr Fp operator-() const noexcept
  {
    return val ? MOD - val : 0;
  }

  constexpr Fp operator+(const Fp &r) const noexcept
  {
    return Fp(*this) += r;
  }

  constexpr Fp operator-(const Fp &r) const noexcept
  {
    return Fp(*this) -= r;
  }

  constexpr Fp operator*(const Fp &r) const noexcept
  {
    return Fp(*this) *= r;
  }

  constexpr Fp operator/(const Fp &r) const noexcept
  {
    return Fp(*this) /= r;
  }

  constexpr Fp &operator+=(const Fp &r) noexcept
  {
    val += r.val;
    if (val >= MOD)
      val -= MOD;
    return *this;
  }

  constexpr Fp &operator-=(const Fp &r) noexcept
  {
    val -= r.val;
    if (val < 0)
      val += MOD;
    return *this;
  }

  constexpr Fp &operator*=(const Fp &r) noexcept
  {
    val = val * r.val % MOD;
    if (val < 0)
      val += MOD;
    return *this;
  }

  constexpr Fp &operator/=(const Fp &r) noexcept
  {
    long long a = r.val, b = MOD, u = 1, v = 0;
    while (b)
    {
      long long t = a / b;
      a -= t * b;
      swap(a, b);
      u -= t * v;
      swap(u, v);
    }
    val = val * u % MOD;
    if (val < 0)
      val += MOD;
    return *this;
  }

  constexpr bool operator==(const Fp &r) const noexcept
  {
    return this->val == r.val;
  }

  constexpr bool operator!=(const Fp &r) const noexcept
  {
    return this->val != r.val;
  }

  friend constexpr ostream &operator<<(ostream &os, const Fp<MOD> &x) noexcept
  {
    return os << x.val;
  }

  friend constexpr istream &operator>>(istream &is, Fp<MOD> &x) noexcept
  {
    return is >> x.val;
  }
};

Fp<MOD> modpow(const Fp<MOD> &a, long long n) noexcept
{
  if (n == 0)
    return 1;
  auto t = modpow(a, n / 2);
  t = t * t;
  if (n & 1)
    t = t * a;
  return t;
}

using mint = Fp<MOD>;

void solve()
{
  ll n, a, b;
  cin >> n >> a >> b;
  if (a > b)
    swap(a, b);

  mint dp2[5010][5010] = {};
  dp2[0][0] = 1;
  rep(i, b) rep(j, b)
  {
    // 0(1)
    dp2[i + 1][j + 1] += dp2[i][j];
    // 0
    if (j == 0 && i != 0)
      dp2[i + 1][j] += dp2[i][j];
    else if (i + a < 5010)
      dp2[i + a][0] += dp2[i][j];
  }

  mint z[5010] = {}; // 1n01
  rep(i, b) rep(j, b) z[i + 2] += dp2[i][j];
  z[0] = z[1] = 1;

  mint dp[5010][2] = {}; // i
  dp[0][0] = dp[0][1] = 1;
  rep(i, n) REP(j, 1, n + 1)
  {
    // 0 -> 1
    if (i + j <= n && j < b)
    {
      if (i == 0 || i + j == n)
        dp[i + j][1] += dp[i][0] * z[j + 1];
      else
        dp[i + j][1] += dp[i][0] * z[j];
    }
    // 1 -> 0
    if (i + j <= n && j < a)
      dp[i + j][0] += dp[i][1];
  }
  mint sum = dp[n][0] + dp[n][1];
  cout << modpow(2, n) - sum << "\n";
}

