			if (B > i) f[i][1] = (f[i][1] + 1ll * f[0][0] * h[i]) % P;
		}
		else {
			for (int j = 1; j <= min(A - 1, i); j++)
				f[i][0] = (f[i][0] + f[i - j][1]) % P;
			for (int j = 1; j <= min(B - 1, i); j++)
				f[i][1] = (f[i][1] + 1ll * f[i - j][0] * h[j]) % P;
		}
	}
	int ans = 1;
	for (int i = 1; i <= n; i++) ans = ans * 2 % P;
	ans = (ans - (f[n][0] + f[n][1]) % P + P) % P;
	cout << ans << endl;
    return 0;
}

