//legal moves:
//0->0, 0->1, 1->0, 1->1, 1->2 via 0-jump, 2->1, 2->2. (can't have two 0-jumps in a row)
ll modExp(ll b, ll ex){
    ll prod = 1;
    while(ex > 0){
        if(ex % 2 == 0){
            b = (b * b) % MOD;
            ex /= 2;
        } else{
            prod = (prod*b) % MOD;
            ex--;
        }
    }

    return prod;
}

