template<int MOD> struct Fp {
    long long val;
    constexpr Fp(long long v = 0) noexcept : val(v % MOD) {
        if (val < 0) val += MOD;
    }
    constexpr int getmod() { return MOD; }
    constexpr Fp operator - () const noexcept {
        return val ? MOD - val : 0;
    }
    constexpr Fp operator + (const Fp& r) const noexcept { return Fp(*this) += r; }
    constexpr Fp operator - (const Fp& r) const noexcept { return Fp(*this) -= r; }
    constexpr Fp operator * (const Fp& r) const noexcept { return Fp(*this) *= r; }
    constexpr Fp operator / (const Fp& r) const noexcept { return Fp(*this) /= r; }
    constexpr Fp& operator += (const Fp& r) noexcept {
        val += r.val;
        if (val >= MOD) val -= MOD;
        return *this;
    }
    constexpr Fp& operator -= (const Fp& r) noexcept {
        val -= r.val;
        if (val < 0) val += MOD;
        return *this;
    }
    constexpr Fp& operator *= (const Fp& r) noexcept {
        val = val * r.val % MOD;
        return *this;
    }
    constexpr Fp& operator /= (const Fp& r) noexcept {
        long long a = r.val, b = MOD, u = 1, v = 0;
        while (b) {
            long long t = a / b;
            a -= t * b; swap(a, b);
            u -= t * v; swap(u, v);
        }
        val = val * u % MOD;
        if (val < 0) val += MOD;
        return *this;
    }
    constexpr bool operator == (const Fp& r) const noexcept {
        return this->val == r.val;
    }
    constexpr bool operator != (const Fp& r) const noexcept {
        return this->val != r.val;
    }
    friend constexpr ostream& operator << (ostream &os, const Fp<MOD>& x) noexcept {
        return os << x.val;
    }
    friend constexpr istream& operator >> (istream &is, Fp<MOD>& x) noexcept {
        return is >> x.val;
    }
    friend constexpr Fp<MOD> modpow(const Fp<MOD> &a, long long n) noexcept {
        if (n == 0) return 1;
        auto t = modpow(a, n / 2);
        t = t * t;
        if (n & 1) t = t * a;
        return t;
    }
};
const int MOD = 1000000007;
//const int MOD = 998244353;
using mint = Fp<MOD>;

void solve() {
	int n, a, b; cin >> n >> a >> b;
    if(a > b) swap(a, b);
    static mint dp[5050][5050][2][2];
    rep(i, 5050) {
        rep(j, 5050) {
            rep(k, 2) {
                rep(l, 2) {
                    dp[i][j][k][l] = 0;
                }
            }
        }
    }
    dp[0][0][1][0] = 1;
    rep(i, n) {
        for(int j = 0; j <= i; ++ j) {
            //0
            if(j + 1 < a) dp[i + 1][j + 1][0][0] += dp[i][j][0][0];

            if(j + 1 < b && j >= a) dp[i + 1][j + 1][0][1] += dp[i][j][0][1];

            if(j < b && 1 < a) dp[i + 1][1][0][0] += dp[i][j][1][0];
            if(j < b && j + a < b) dp[i + a][j + a][0][1] += dp[i][j][1][0];
            //1
            if(j < a && 1 < b) dp[i + 1][1][1][0] += dp[i][j][0][0];
            if(j + 1 < b) dp[i + 1][j + 1][1][0] += dp[i][j][0][1];
            if(j + 1 < b) dp[i + 1][j + 1][1][0] += dp[i][j][1][0];
        }
    }
    mint ans = modpow((mint)2, n);
    for(int i = 0; i <= n; ++ i) {
        rep(j, 2) {
            rep(k, 2) {
                ans -= dp[n][i][j][k];
            }
        }
    }
    cout << ans << endl;
}

