modint<MAX_MOD> dp[5001];
modint<MAX_MOD> two[10000];
void solve(){
    two[0] = 1;
    for (int q = 1; q < 10000; ++q) {
        two[q] = two[q - 1] * 2;
    }
    int n, a, b;
    cin >> n >> a >> b;
    if (a > b) swap(a, b);

    dp[0] = 1;
    dp[1] = MAX_MOD - 1;
    modint<MAX_MOD> ans = 0;
    REP(i, n) {
        dp[i + 1] += dp[i];
        modint<MAX_MOD> geko[10001] = {};
        geko[i] += dp[i];
        geko[i + 1] -= dp[i];
        for (int q = i; q <= n+1; ++q) {
            geko[q + 1] += geko[q];
            if (min(q,n) - max(i - 1LL, 0LL) >= b) {
                ans += geko[q] * two[max(n - q, 0LL)];
                continue;
            }
            if (q != n) {
                //next -> white
                geko[q + 1] += geko[q];
                geko[q + 2] -= geko[q];
            }
            dp[q + 1 + 1] += geko[q];
            dp[q + a + 1] -= geko[q];
            /*
            for (int red = 1; red < a; ++red) {
                dp[q + red + 1] += geko[q];
            }
            */
            geko[q + a + 1] += geko[q];
        }
    }
    cout << ans << endl;
}
