template<typename T>
struct Polynomial : vector<T> {
  using vector<T>::vector;
  using P = Polynomial;
  P operator-() const {
    P res((*this).size());
    rep(i, (*this).size()) res[i] = -(*this)[i];
    return res;
  }
  P &operator+=(const P q) {
    rep(i, (*this).size()) if (i < q.size()) (*this)[i] += q[i];
    return *this;
  }
  P &operator-=(const P q) {
    rep(i, (*this).size()) if (i < q.size()) (*this)[i] -= q[i];
    return *this;
  }
  P &operator*=(const P q) {
    drep(i, (*this).size()) {
      (*this)[i] *= q[0];
      rep2(j, 1, q.size()) if (j <= i) (*this)[i] += q[j] * (*this)[i-j];
    }
    return *this;
  }
  P &operator/=(const P q) {
    rep(i, (*this).size()) {
      rep2(j, 1, q.size()) if (j <= i) (*this)[i] -= q[j] * (*this)[i-j];
      (*this)[i] /= q[0];
    }
    return *this;
  }
  P operator+(const P q) const { return P(*this) += q; }
  P operator-(const P q) const { return P(*this) -= q; }
  P operator*(const P q) const { return P(*this) *= q; }
  P operator/(const P q) const { return P(*this) /= q; }
  P &mul(map<int, T> q) {
    drep(i, (*this).size()) {
      (*this)[i] *= q[0];
      for (auto &[j, b]: q) if (j != 0 && j <= i) (*this)[i] += b * (*this)[i-j];
    }
    return *this;
  }
  P &div(map<int, T> q) {
    rep(i, (*this).size()) {
      for (auto &[j, b]: q) if (j != 0 && j <= i) (*this)[i] -= b * (*this)[i-j];
      (*this)[i] /= q[0];
    }
    return *this;
  }
