			for(int j=1;j <= i && j < B;++j) {
				if(j < A) f[i][j][0]=sum1[i-j][i-j][1];
				if(j >= A) f[i][j][0]=sum2[i-j][j-A][1];
				f[i][j][1]=sum1[i-j][min(A-1,i-j)][0];
				if(j >= A)
					f[i][j][1]=((LL)f[i][j][1]+sum2[i-j][j-1][0]+mod-sum2[i-j][A-1][0])%mod;
			}
