		rep(k,3) dp[i][j][k]%=mo;
		if(j>=a){
			rep(k,3){
				out+=dp[i][j][k]*zyo[n-i];
				out%=mo;
			}
			continue;
		}
		if(i>=n) continue;

		dp[i+1][1][1]+=dp[i][j][0];
		if(j<b-1){
			dp[i+1][j+1][0]+=dp[i][j][0];
		}

		dp[i+1][j+1][1]+=dp[i][j][1];
		dp[i+1][1][0]+=dp[i][j][1];
		if(i+b<=n) dp[i+b][j+b][2]+=dp[i][j][1];

		dp[i+1][j+1][1]+=dp[i][j][2];
		dp[i+1][j+1][2]+=dp[i][j][2];
	}
	cout<<out<<endl;
}

