                ans %= MOD;
                //                cout << i << " " << j << " " << dp[i][j] <<
                //                endl;
                dp[i][j] = 0;
            }
            (dpxsum[i][j] = dpxsum[i - 1][j] + dp[i][j]) %= MOD;
            (dpysum[i][j] = dpysum[i][j - 1] + dp[i][j]) %= MOD;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (n - i + 1 >= a && n - j >= b) {
                (ans += dp[i][j]) %= MOD;
                //                cout << i << " " << j << " " << dp[i][j] <<
                //                endl;
            }
            //            cout << dp[i][j] << " ";
        }
        //        cout << endl;
    }
    if (ans < 0)
        ans += MOD;
    cout << ans << endl;

    return 0;
}

