template<typename T> T MV(T v) { return v; }
template<typename T, typename ... U> auto MV(T v, int a, U ... b) { return vector<decltype(MV(v, b ...))>(a, MV(v, b ...)); }

int main() {
	IN(int, n, a, b);
	if_not(a <= b) { swap(a, b); }

	if(a == 1) { OUT(MI(2) ^ n); exit(0); }

	MI ans = 0, s = 1, t = 1;
	auto dp_ = MV<MI>(0, n + a + 1, n + b + 1);
	dp_[n][n] = 1;
	decII(k, 0, n) {
		auto dp = [&](int i, int j) -> MI & { return dp_[k + i][k + j]; };

		ans += (dp(0, b) + dp(a, b)) * (MI(2) ^ k);
		s -= dp(0, b) + dp(a, b);
		t -= dp(0, b);
		dp(0, b) = 0;
		incID(i, 1, a) { dp(i, b - 1) += dp(i, b); }

		if(k == 0) { break; }

		MI u = 0, tt = 0;
		inc(j, b) {
			MI v = dp(0, j) + dp(a, j);
			dp(-1, j) += v;
			tt += v;

			dp(a - 1, j) += dp(a, j);
			u += dp(a, j);
		}
		dp(-1, 0) += s - (t + u);
		tt += s - (t + u);

		s *= 2;
		t = tt;
	}
	OUT(ans);
}

