				for (int y=A+1; y<=j; y++)
					dp[i][j]+=dp[i-y][j-y];
			}
			else if (j>A+1){
				dp[i][j]+=dp[i-1][j-1]-dp[i-2][j-2];
				dp[i][j]+=dp[i-A-1][j-A-1];
			}

			dp[i][j]%=mod;
		}
		if (A<=i && i<B) dp[i][i]++;

		for (int j=0; j<B; j++) sum[i]=(sum[i] + dp[i][j])%mod;
	}

	ans=1;
	for (int i=1; i<=n; i++) ans=ans*2%mod;
	ans=(ans-sum[n])%mod;
	if (ans<0) ans+=mod;
	cout<<ans<<"\n";

	return 0;
}

