ll sum[MAX];
ll naname[MAX][MAX];

int main(){

    std::ifstream in("text.txt");
    std::cin.rdbuf(in.rdbuf());
    cin.tie(0);
    ios::sync_with_stdio(false);

    int N,A,B;cin>>N>>A>>B;
    if(A>B) swap(A,B);
    if(A==1){
        cout<<rui(2,N)<<endl;
        return 0;
    }
    dp[0][0]=1;
    sum[0]=1;
    naname[0][0]=1;

    for(int i=1;i<=N;i++){
        for(int j=0;j<B;j++){
            if(j) dp[i][j]+=dp[i-1][j-1];

            if(j==1){
                if(i-2>=0) dp[i][j]+=sum[i-2];
                if(i-(A+1)>=0) dp[i][j]+=mod-sum[i-(A+1)];
            }

            if(i-(A+1)>=0&&j-(A+1)>=0) dp[i][j]+=naname[i-(A+1)][j-(A+1)];

            while(dp[i][j]>=mod) dp[i][j]-=mod;
        }

        ll S=0;
        for(int l=0;l<=B-1;l++){
            S+=dp[i][l];
        }
        S%=mod;
        sum[i]=sum[i-1]+S;
        if(sum[i]>=mod) sum[i]-=mod;

        naname[i][0]=dp[i][0];
        for(int j=1;j<=N;j++){
            naname[i][j]=naname[i-1][j-1]+dp[i][j];
            if(naname[i][j]>=mod) naname[i][j]-=mod;
        }
    }

    ll ans=0;

    for(int i=1;i<N;i++){
        for(int j=1;j<B;j++){
            if(N-i<A||j+N-i<B){
                ans+=dp[i][j];
            }
        }
    }

    for(int j=0;j<B;j++){
        ans+=dp[N][j];
    }

    ans%=mod;

    cout<<(mod+rui(2,N)-ans)%mod<<endl;
}

