  f[0][0][1] = g[0] = ModInt(1);
  for (int i = 1; i <= n; ++i) {
    f[i][1][1] += (i >= 2 ? g[i - 2] : ModInt()) - (i >= a + 1 ? g[i - a - 1] : ModInt());

    for (int j = 0; j <= n; ++j) {
      if (i >= a && j >= a) {
        f[i][j][0] += f[i - a][j - a][1];
      }

      if (j) {
        f[i][j][0] += f[i - 1][j - 1][0];

        f[i][j][1] += f[i - 1][j - 1][0];
        f[i][j][1] += f[i - 1][j - 1][1];
      }
    }

    for (int j = 0; j < b; ++j) {
      g[i] += f[i][j][1];
    }
    g[i] += g[i - 1];
  }

  vec<ModInt, 1> pw2(n + 1);
  pw2[0] = ModInt(1);
  for (int i = 1; i < sz(pw2); ++i) {
    pw2[i] = pw2[i - 1] + pw2[i - 1];
  }


  ModInt ans;
  for (int i = 1; i < n; ++i) {
    ModInt sum;
    for (int j = b; j <= n; ++j) {
      sum += f[i][j][1];
    }

    for (int j = i + 1; j <= n && j - i < a; ++j) {
      ans += sum * pw2[max(0, n - j - 1)];
    }
  }
  for (int j = b; j <= n; ++j) {
    ans += f[n][j][0] + f[n][j][1];
  }

  cout << ans << '\n';

  return 0;
}

