	INIT;

	VAR(i64, n, a, b);
	if (a < b) std::swap(a, b);

	dp[0][0] = 1;
	c[1][1] = 1;
	c2[0] = 1;
	FOR(i, 1, n + 1) FOR(j, 1, a) {
		if (j == 1) {
			if (i == 1) {
				(dp[i][j] += dp[i - 1][j - 1]) %= MOD;
			}
			else {
				FOR(k, 2, b + 1) {
					if (i - k < 0) break;
					(dp[i][j] += c2[i - k]) %= MOD;
				}
			}
		}
		else {
			(dp[i][j] += dp[i - 1][j - 1]) %= MOD;
			if (i - b >= 0 && j - b >= 0) (dp[i][j] += c[i - b][j - b]) %= MOD;
		}

		(c[i + 1][j + 1] += c[i][j] + dp[i][j]) %= MOD;
		(c2[i] += dp[i][j]) %= MOD;
	}
	i64 sub = 0;
	REP(i, n + 1) FOR(j, 1, a) {
		i64 t = j + (n - i >= b ? n - i : 0);
		if (t >= a) continue;
		(sub += dp[i][j]) %= MOD;
	}
	i64 ans = 1;
	REP(_, n) (ans *= 2) %= MOD;
	(ans -= sub) %= MOD;
	if (ans < 0) ans += MOD;
	OUT(ans)BR;

	return 0;
}

