const int N = 5050, mod = 1e9+7;
int n,A,B,f[N][2],g[N][2];

inline int Mod(int x){
	return x>=mod?x-mod:x;
}
inline void upd(int &x,int y){
	x+=y;
	if (x>=mod) x-=mod;
}

int main(){
	n=read(),A=read(),B=read();
	if (A>B) swap(A,B);
	g[0][0]=1;
	FOR(i,1,B){
		For(j,1,i) upd(g[i][1],g[i-j][0]);
		For(j,A,i) upd(g[i][0],g[i-j][1]);
	}
	f[0][0]=f[0][1]=1;
	For(i,1,n){
		For(j,1,min(i,A-1)) upd(f[i][0],f[i-j][1]);
		For(j,1,min(i,B-1)){
			f[i][1]=(f[i][1]+1ll*f[i-j][0]*g[j][1])%mod;
			if (i==j) upd(f[i][1],g[i][0]);
			if (i==n) f[i][0]=(f[i][0]+1ll*g[j][0]*f[i-j][0])%mod;
		}
	}
	int ans=1;
	For(i,1,n) ans=2*ans%mod;
	printf("%d\n",Mod(ans+mod-Mod(f[n][0]+f[n][1])));
}

