int main(){
    ios::sync_with_stdio(false);cin.tie(0);cout.tie(0);
    ll n,a[2];cin>>n>>a[0]>>a[1];
    if(a[0]<a[1])swap(a[0],a[1]);
    rep(i,0,n+10)rep(j,0,n+10)dp[i][j]=0;
    dp[0][0]=1;
    ll sum1[n+10];
    rep(i,0,n+10)sum1[i]=0;
    sum1[0]=1;
    ll sum2[n+10][n+10];
    rep(i,0,n+10)rep(j,0,n+10)sum2[i][j]=0;
    sum2[0][0]=1;
    rep(i,1,n+1){
        rep(j,1,a[0]){
            if(j==1){
                ll le=max(i-a[1],(ll)0);
                if(i==1){
                    dp[i][j]=1;
                }
                else{
                    if(le==0)dp[i][j]=sum1[i-2];
                    else dp[i][j]=(sum1[i-2]-sum1[le-1]+mod)%mod;
                }
            }
            else if(j<=a[1]){
                dp[i][j]=dp[i-1][j-1];
            }
            else{
                if(i<j)continue;
                dp[i][j]=(sum2[i-j][i-(a[1]+1)]+dp[i-1][j-1])%mod;
            }
        }
        sum1[i]=sum1[i-1];
        rep(j,1,min(a[0],(ll)i+1)){
            (sum1[i]+=dp[i][j])%=mod;
            sum2[i-j][i]=(sum2[i-j][i-1]+dp[i][j])%mod;
        }
    }
    ll ans=beki(2,n,mod);
    ll sum=0;
    rep(i,0,n+1){
        rep(j,0,n+1){
            ll noko=n-i;
            if(noko<a[1])noko=0;
            if(j+noko<a[0]){
                (sum+=dp[i][j])%=mod;
            }
        }
    }
    ans=ans-sum+mod;
    cout<<ans%mod<<endl;
}

