using MI = ModInt<1'000'000'007>;

template<typename T> T MV(T v) { return v; }
template<typename T, typename ... U> auto MV(T v, int a, U ... b) { return vector<decltype(MV(v, b ...))>(a, MV(v, b ...)); }

int main() {
	IN(int, n, a, b);
	if_not(a <= b) { swap(a, b); }

	if(a == 1) { OUT(MI(2) ^ n); exit(0); }

	MI ans = 0, s = 0;
	auto dp = MV<MI>(0, n + 1, n + 1);
	dp[0][0] = 1;
	incII(k, 0, n) {
		if(k >= b) {
			auto & v = dp[k - b][k - a];
			auto & w = dp[k - b][k - 0];
			ans += v * (MI(2) ^ (n - k));
			ans += w * (MI(2) ^ (n - k));
			s -= v;
			v = 0;
			w = 0;
			incID(j, k - a + 1, k) {
				dp[k - b + 1][j] += dp[k - b][j];
				dp[k - b][j] = 0;
			}
		}
		if(k == n) { break; }

		MI t = 0;
		int il = max(0, k - b + 1);
		if(k >= a) {
			incII(i, il, k) {
				MI & v = dp[i][k - a];
				s -= v;
				t += v;
				dp[i][k - a + 1] += v;
				dp[i][k + 1] += v;
				v = 0;
			}
		}
		dp[k][k + 1] += s;
		s += t;
		{
			incII(i, il, k) {
				MI v = dp[i][k];
				s += v;
				dp[i][k + 1] += v;
			}
		}
	}
	OUT(ans);
}

