		}
	}
	int ans = 0;
	for(int i = 0; i <= N; i++)
	{
		if(i == 0)
		{
			for(int j = B; j < A + B; j++)
			if(i + j <= N)
			{
				ans += 1ll * dp[i][1] * (g[j][0] + g[j][1]) % mod * _2[N - i - j] % mod;
				if(ans >= mod)
					ans -= mod;
			}
		}
		for(int j = B; j < A + B; j++)
		if(i + j <= N)
