int N, A, B;
MInt DP[5009][5009];
MInt DQ[5009][5009];
MInt EP[5009][5009];
MInt EQ[5009][5009];
MInt S[5009];
int main() {
	cin >> N >> A >> B;
	if (A < B) swap(A, B);

	DP[0][0] = 1;
	DQ[0][0] = 1;
	EP[0][0] = 1;
	EQ[0][0] = 1;
	S[0] = 1;
	REP(i, 1, N + 1) {
		REP(k, 0, A) {
			if (k > i) continue;
			if (k == 0) {
				DQ[i][0] += S[i - 1];
				if (i - B >= 0) {
					DQ[i][0] -= S[i - B];
				}
			} else {
				int a = B;
				int c = k + 1;
				if (k - a >= 0) DQ[i][k] += EP[i - a][k - a];
				if (k - c >= 0) DQ[i][k] -= EP[i - c][k - c];
			}

			int a = 1;
			int c = k + 1;
			if (k - a >= 0) DP[i][k] += EQ[i - a][k - a];
			if (k - c >= 0) DP[i][k] -= EQ[i - c][k - c];

			EP[i][k] = DP[i][k];
			EQ[i][k] = DQ[i][k];
			if (i > 0 && k > 0) {
				EP[i][k] += EP[i - 1][k - 1];
				EQ[i][k] += EQ[i - 1][k - 1];
			}
		}

		S[i] = S[i - 1];
		REP(k, 0, A) {
			S[i] += DP[i][k];
		}
	}

	MInt sum = MInt(2)[N];
	REP(k, 0, A) {
		sum -= DP[N][k];
		sum -= DQ[N][k];
	}
	cout << sum << endl;
	return 0;
}

