bool visA[5001][2];
mint<MOD> dpA[5001][2];
mint<MOD> countA(int L, int k){
    if(L == 0){
        if(k) return 1;
        else return -1;
    }
    if(L < A) return 0;
    if(visA[L][k]) return dpA[L][k];
    visA[L][k] = true;
    mint<MOD> ret = 0;
    for(int i=1; i<=A && L-i>=0; i++){
        ret += countA(L-i, k^1);
    }
    return dpA[L][k] = ret;
}

bool visB[5001][2];
mint<MOD> dpB[5001][2];
mint<MOD> countB(int L, int k){
    if(L == 0){
        if(k) return 1;
        else return -1;
    }
    if(L < B) return 0;
    if(visB[L][k]) return dpB[L][k];
    visB[L][k] = true;
    mint<MOD> ret = 0;
    for(int i=1; i<=B && L-i>=0; i++){
        ret += countB(L-i, k^1);
    }
    return dpB[L][k] = ret;
}

bool vis[5001][3][2];
mint<MOD> dp[5001][3][2];
mint<MOD> solve(int l, int b, int a){
    if(l == N){
        if(a == 0) return 0;
        return mint<MOD>(-1);
    }
    if(vis[l][b][a]) return dp[l][b][a];
    vis[l][b][a] = true;
    mint<MOD> ret = solve(l+1, 2, a)*2;
    for(int i=1; l+i<=N; i++){
        if(i >= A && b != 0){
            ret -= solve(l+i, 0, 1)*countA(i,0);
        }
        if(i >= B && b != 1){
            ret -= solve(l+i, 1, 1)*countB(i,0);
        }
    }
    return dp[l][b][a] = ret;
}

