int n,a,b,i,j,k,f[5005],g[5005],pw[5005],ans;
int main(){
	cin>>n>>a>>b;
	if(a>b)swap(a,b);
	f[0]=1;g[0]=1;pw[0]=1;
	rep(i,n){
		pw[i]=2ll*pw[i-1]%mod;
		f[i]=pw[i-1];g[i]=pw[i-1];
		f[i]=(f[i]-ans+mod)%mod;
		g[i]=(g[i]-ans+mod)%mod;
		ans=2ll*ans%mod;
		if(i>=b){
			g[i]=(g[i]-f[i-b]+mod)%mod;
			ans=(ans+f[i-b])%mod;
			for(j=a;j<=b;j++){
				int tmp=f[i-b];
				if(j==a){
					tmp=0;
					for(k=b;k<=i&&k<a+b;k++){
						(tmp+=f[i-k])%=mod;
					}
				}
				if(j==b){
					if(a!=b) tmp=(i==b);
					else tmp=g[i-b];
				}
				f[i]=(f[i]-tmp+mod)%mod;
				ans=(ans+tmp)%mod;
			}
		}
	}
	cout<<ans<<endl;
	return 0;
}

