int N, A, B;

bool vis[5001][5001][2][2];
int dp[5001][5001][2][2];

int sum1(short, short, bool, bool);
int sum2(short, short, bool, bool);

int solve(short n, short k, bool b, bool a){
    if(n == N){
        if(a == 1 || k >= A) return 1;
        else return 0;
    }
    if(vis[n][k][b][a]) return dp[n][k][b][a];
    vis[n][k][b][a] = true;
    bool a2 = a;
    if(k >= A) a2 = true;
    ll ret = solve(n+1, k+1, 0, a2);
    if(b == 0){
        ret += sum1(n+1, 0, 1, a2) - sum1(n+min(B,N-n+1), 0, 1, a2);
        ret += sum2(n+min(B, N-n+1), k+min(B, N-n+1), 1, a2) - sum2(N+1, k+N-n+1, 1, a2);
    }
    if(ret < 0) ret += MOD;
    if(ret < 0) ret += MOD;
    if(ret >= MOD) ret -= MOD;
    if(ret >= MOD) ret -= MOD;
    return dp[n][k][b][a] = ret;
}

bool vis1[2][5001][5001];
int dp1[2][5001][5001];
int sum1(short n, short k, bool b, bool a){
    if(n > N) return 0;
    if(vis1[a][n][k]) return dp1[a][n][k];
    vis1[a][n][k] = true;
    int ret = solve(n, k, b, a) + sum1(n+1, k, b, a);
    if(ret >= MOD) ret -= MOD;
    return dp1[a][n][k] = ret;
}

bool vis2[2][5001][5001];
int dp2[2][5001][5001];
int sum2(short n, short k, bool b, bool a){
    if(n > N) return 0;
    if(vis2[a][n][k]) return dp2[a][n][k];
    vis2[a][n][k] = true;
    int ret = solve(n, k, b, a) + sum2(n+1, k+1, b, a);
    if(ret >= MOD) ret -= MOD;
    return dp2[a][n][k] = ret;
}

