const int N = 5005;
int dp[N][N], dp2[N][N], prefdp[N];
int p2[N];
const int mod = 1e9 + 7;
inline int add(int x, int y){ x += y; if(x >= mod) x -= mod; return x;}
inline int sub(int x, int y){ x -= y; if(x < 0) x += mod; return x;}
inline int mul(int x, int y){ return (x * 1ll * y) % mod;}
inline int powr(int a, ll b){
	int x = 1 % mod;
	while(b){
		if(b & 1) x = mul(x, a);
		a = mul(a, a);
		b >>= 1;
	}
	return x;
}
inline int inv(int a){ return powr(a, mod - 2);}

int main(){
	p2[0] = 1;
	for(int i = 1; i < N; i++) p2[i] = add(p2[i - 1], p2[i - 1]);
	int n, A, B; cin >> n >> A >> B;
	if(A > B) swap(A, B);
	for(int j = 0; j <= n; j++) dp[j][B] = dp2[j][B] = p2[j];
	for(int j = 1; j <= n; j++){
		for(int i = 0; i < B; i++){
			dp[j][i] = dp[j - 1][i + 1];
			// < A zeroes, then one
			dp[j][i] = add(dp[j][i], sub(
				j >= 2 ? prefdp[j - 2] : 0, j - A - 1 < 0 ? 0 : prefdp[j - A - 1]));
			// atleast A zeroes
			if(j >= A){
				dp[j][i] = add(dp[j][i], dp2[j - A][min(B, i + A)]);
			}
			dp2[j][i] = add(dp2[j - 1][i + 1], dp[j - 1][i + 1]);
			if(i == 1) prefdp[j] = add(prefdp[j - 1], dp[j][i]);
		}
	}
	cout << dp[n][0] << endl;
}

