mint<MOD> sum1(int, int, int, int);
mint<MOD> sum2(int, int, int, int);

mint<MOD> solve(int n, int k, int b, int a){
    if(n == N){
        if(a == 1 || k >= A) return 1;
        else return 0;
    }
    if(vis[n][k][b][a]) return dp[n][k][b][a];
    vis[n][k][b][a] = true;
    int a2 = a;
    if(k >= A) a2 = 1;
    mint<MOD> ret = solve(n+1, k+1, 0, a2);
    if(b == 0){
        /*for(int i=1; n+i<=N; i++){
            if(i < B){
                ret += solve(n+i, 0, 1, a2);
            }
            else{
                ret += solve(n+i, k+i, 1, a2);
            }
        }*/
        ret += sum1(n+1, 0, 1, a2) - sum1(n+min(B,N-n+1), 0, 1, a2);
        ret += sum2(n+min(B, N-n+1), k+min(B, N-n+1), 1, a2) - sum2(N+1, k+N-n+1, 1, a2);
    }
    return dp[n][k][b][a] = ret;
}

bool vis1[5001][5001][2];
mint<MOD> dp1[5001][5001][2];
mint<MOD> sum1(int n, int k, int b, int a){
    if(n > N) return 0;
    if(vis1[n][k][a]) return dp1[n][k][a];
    vis1[n][k][a] = true;
    return dp1[n][k][a] = solve(n, k, b, a) + sum1(n+1, k, b, a);
}

bool vis2[5001][5001][2];
mint<MOD> dp2[5001][5001][2];
mint<MOD> sum2(int n, int k, int b, int a){
    if(n > N) return 0;
    if(vis2[n][k][a]) return dp2[n][k][a];
    vis2[n][k][a] = true;
    return dp2[n][k][a] = solve(n, k, b, a) + sum2(n+1, k+1, b, a);
}

